import React, { Component } from 'react'
import styled from 'styled-components'
import axios from 'axios'
require('dotenv').config()

const FormContainer = styled.form`

`


class FormLogin extends Component {
    state={
        username:'',
        password:'',
        msg:''
    }
    handleChangeLogin =(e)=>{
        e.preventDefault()
        const {name, value} = e.target

        this.setState({
            [name]:value
        })
    }

    handleSubmit = async (e) =>{
        e.preventDefault()
        const payload = {
            username:this.state.username,
            passwordHash:this.state.password
        }
        console.log(`${process.env.API}/login`)
        const a = await axios.post(`${process.env.API}/login`, payload)

        console.log(a)
        console.log('parou aqui')
        console.log(a.data)

    }

    render() {
        return (
            <div>
                <FormContainer>
                    <label>Usuário</label>
                    <input type='text' name='username' value={this.state.username} onChange={this.handleChangeLogin}></input>
                    <label>Senha</label>
                    <input type='password' name='password' value={this.state.password} onChange={this.handleChangeLogin}></input>
                    <button type='submit' onClick={this.handleSubmit}>Entrar</button>
                </FormContainer>
            </div>
        )
    }
}

export default FormLogin
